import axios from "axios";
import { useEffect, useState } from "react";

export default function AdminPayments(){

  const [payments,setPayments] = useState([]);
  const token = localStorage.getItem("adminToken");

  const loadPayments = ()=>{
    axios.get("http://localhost:5000/admin/payments",{
      headers:{ Authorization:`Bearer ${token}` }
    })
    .then(res => setPayments(res.data))
    .catch(err => console.log(err));
  };

  useEffect(()=>{ loadPayments(); },[]);


  const approvePayment = (id)=>{
    axios.put(`http://localhost:5000/admin/payment/approve/${id}`,{},{
      headers:{ Authorization:`Bearer ${token}` }
    }).then(()=>{
      alert("Payment Approved");
      loadPayments();
    });
  };


  const rejectPayment = (id)=>{
    if(!window.confirm("Reject payment?")) return;

    axios.put(`http://localhost:5000/admin/payment/reject/${id}`,{},{
      headers:{ Authorization:`Bearer ${token}` }
    }).then(()=>{
      alert("Payment Rejected");
      loadPayments();
    });
  };


  return(
    <div className="container mt-4">

      <h2>Payment Requests</h2>
      <hr/>

      <table className="table table-bordered table-striped">
        <thead>
          <tr>
            <th>User</th>
            <th>Room</th>
            <th>Checkin</th>
            <th>Checkout</th>
            <th>Transaction</th>
            <th>Proof</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>
          {payments.map(p=>(
            <tr key={p.id}>
              <td>{p.userName}</td>
              <td>{p.roomName}</td>
              <td>{p.checkIn}</td>
              <td>{p.checkOut}</td>

              <td>{p.transactionId || "N/A"}</td>

              <td>
                {p.paymentProof ? (
                  <a
                    href={`http://localhost:5000/payments/${p.paymentProof}`}
                    target="_blank"
                    rel="noreferrer"
                    className="btn btn-sm btn-primary">
                    View
                  </a>
                ) : "No File"}
              </td>

              <td>
                <span className={`badge ${
                  p.paymentStatus === "Paid"
                  ? "bg-success"
                  : p.paymentStatus === "Rejected"
                  ? "bg-danger"
                  : "bg-warning"
                }`}>
                  {p.paymentStatus}
                </span>
              </td>

              <td>
                {p.paymentStatus === "Pending Verification" && (
                  <>
                    <button
                      className="btn btn-success btn-sm mx-1"
                      onClick={()=>approvePayment(p.id)}>
                      Approve
                    </button>

                    <button
                      className="btn btn-danger btn-sm"
                      onClick={()=>rejectPayment(p.id)}>
                      Reject
                    </button>
                  </>
                )}

                {p.paymentStatus !== "Pending Verification" && "—"}
              </td>

            </tr>
          ))}
        </tbody>

      </table>
    </div>
  );
}
