import axios from "axios";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";

function Booking(){

  const {id} = useParams();
  const [room,setRoom] = useState(null);
  const [customerName,setName] = useState("");
  const [checkIn,setCheckIn] = useState("");
  const [checkOut,setCheckOut] = useState("");

  // Load room details to get price
  useEffect(()=>{
    axios.get(`http://localhost:5000/rooms`)
      .then(res=>{
        const r = res.data.find(x=>x.id == id);
        setRoom(r);
      });
  },[]);


  function payNow(){

    if(!customerName || !checkIn || !checkOut){
      return alert("Please fill all details");
    }

    axios.post("http://localhost:5000/create-order",{
      amount: room.price
    })
    .then(res=>{
      const order = res.data;

      var options = {
        key: "YOUR_RAZORPAY_KEY",
        amount: order.amount,
        currency: "INR",
        name: "Hotel Seven Hills",
        description: "Room Booking Payment",
        order_id: order.id,

        handler: function (response){

          axios.post("http://localhost:5000/verify-payment", response)
          .then(result => {
              if(result.data.message === "Payment Verified"){

                axios.post("http://localhost:5000/book",{
                  customerName,
                  roomId:id,
                  checkIn,
                  checkOut
                }).then(()=>{
                  alert("Booking Successful üéâ");
                  window.location = "/bookings";
                });
              }
              else{
                alert("Payment Failed ‚ùå");
              }
          });
        },

        prefill: {
          name: customerName
        },

        theme: {
          color: "#ffc107"
        }
      };

      var rzp = new window.Razorpay(options);
      rzp.open();

    });
  }


  if(!room) return <h3 className="text-center mt-5">Loading Room...</h3>

  return(
    <div className="container mt-4">
      <h2>Book Room</h2>

      <h4 className="mt-2">Room: {room.name}</h4>
      <h5>Price: ‚Çπ{room.price}</h5>

      <input 
        className="form-control mt-2"
        placeholder="Your Name"
        onChange={(e)=>setName(e.target.value)}
      />

      <label className="mt-3">Check In</label>
      <input 
        type="date" 
        className="form-control"
        onChange={(e)=>setCheckIn(e.target.value)} 
      />

      <label className="mt-3">Check Out</label>
      <input 
        type="date" 
        className="form-control"
        onChange={(e)=>setCheckOut(e.target.value)} 
      />

      <button className="btn btn-warning mt-3 w-100" onClick={payNow}>
        Pay ‚Çπ{room.price} & Confirm Booking
      </button>

    </div>
  );
}

export default Booking;
