import axios from "axios";
import { useEffect, useState } from "react";

export default function AdminBookings() {

  const [bookings, setBookings] = useState([]);

  const token = localStorage.getItem("adminToken");

  const loadBookings = () => {
    axios.get("http://localhost:5000/admin/bookings", {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => setBookings(res.data))
      .catch(() => alert("Unauthorized"));
  };

  useEffect(() => {
    loadBookings();
  }, []);

  const approveBooking = (id) => {
    axios.put(`http://localhost:5000/admin/approve/${id}`)
      .then(() => {
        alert("Booking Approved & Room Locked");
        loadBookings();
      });
  };

  const rejectBooking = (id) => {
    axios.put(`http://localhost:5000/admin/reject/${id}`)
      .then(() => {
        alert("Booking Rejected & Room Released");
        loadBookings();
      });
  };

  return (
    <div className="container mt-4">
      <h2>Admin Bookings</h2>

      <table className="table table-bordered mt-3">
        <thead>
          <tr>
            <th>Customer</th>
            <th>Room</th>
            <th>Check In</th>
            <th>Check Out</th>
            <th>Status</th>
            <th>Payment</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>
          {bookings.map(b => (
            <tr key={b.id}>
              <td>{b.customerName}</td>
              <td>{b.roomName}</td>
              <td>{b.checkIn}</td>
              <td>{b.checkOut}</td>

              <td>
                <span className={`badge ${b.status === "Approved" ? "bg-success" :
                  b.status === "Rejected" ? "bg-danger" : "bg-warning"}`}>
                  {b.status}
                </span>
              </td>

              <td>
                <span className={`badge ${b.paymentStatus === "Paid" ? "bg-success" :
                  b.paymentStatus === "Rejected" ? "bg-danger" : "bg-secondary"}`}>
                  {b.paymentStatus}
                </span>
              </td>

              <td>
                {b.status === "Pending" &&
                  <>
                    <button className="btn btn-success btn-sm mx-1"
                      onClick={() => approveBooking(b.id)}>
                      Approve
                    </button>

                    <button className="btn btn-danger btn-sm"
                      onClick={() => rejectBooking(b.id)}>
                      Reject
                    </button>
                  </>
                }
              </td>

            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
