import axios from "axios";
import { useEffect, useState } from "react";

export default function AdminBookings(){

  const [bookings,setBookings] = useState([]);

  useEffect(()=>{
    axios.get("http://localhost:5000/admin/bookings")
    .then(res=>setBookings(res.data))
  },[]);

  function approve(id){
    axios.put(`http://localhost:5000/admin/approve/${id}`)
    .then(()=>{
      alert("Approved");
      window.location.reload();
    })
  }

  function reject(id){
    axios.put(`http://localhost:5000/admin/reject/${id}`)
    .then(()=>{
      alert("Rejected");
      window.location.reload();
    })
  }

  function cancel(id){
    axios.put(`http://localhost:5000/admin/cancel/${id}`)
    .then(()=>{
      alert("Cancelled");
      window.location.reload();
    })
  }

  return(
    <div className="container mt-4">
      <h2>Manage Bookings</h2>

      <table className="table table-bordered mt-3">
        <thead>
          <tr>
            <th>User</th>
            <th>Room</th>
            <th>CheckIn</th>
            <th>CheckOut</th>
            <th>Status</th>
            <th>Payment</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>
          {bookings.map(b=>(
            <tr key={b.id}>
              <td>{b.customerName}</td>
              <td>{b.roomName}</td>
              <td>{b.checkIn}</td>
              <td>{b.checkOut}</td>

              <td>
                <span className={`badge ${
                  b.status==="Approved"?"bg-success":
                  b.status==="Pending"?"bg-warning":
                  "bg-danger"
                }`}>
                  {b.status}
                </span>
              </td>

              <td>{b.paymentStatus}</td>

              <td>
                {b.status==="Pending" &&
                  <>
                    <button className="btn btn-success btn-sm me-2" onClick={()=>approve(b.id)}>Approve</button>
                    <button className="btn btn-danger btn-sm" onClick={()=>reject(b.id)}>Reject</button>
                  </>
                }

                {b.status==="Approved" &&
                  <button className="btn btn-dark btn-sm" onClick={()=>cancel(b.id)}>Cancel</button>
                }
              </td>

            </tr>
          ))}
        </tbody>

      </table>
    </div>
  );
}
