import React from "react";

export default function Home(){

  const goRooms = () =>{
    const token = localStorage.getItem("userToken");
    if(token){
      window.location = "/rooms";
    }else{
      alert("Please login first");
      window.location = "/login";
    }
  }

  return(
    <>
    
    {/* HERO SECTION */}
    <div className="hero-section text-white d-flex align-items-center justify-content-center"
      style={{
        height:"90vh",
        backgroundImage:`url('https://images.unsplash.com/photo-1551776235-dde6d4829808')`,
        backgroundSize:"cover",
        backgroundPosition:"center",
        position:"relative"
      }}
    >

      <div style={{
        position:"absolute",
        top:0,left:0,right:0,bottom:0,
        background:"rgba(0,0,0,.6)"
      }}/>

      <div className="text-center" style={{zIndex:5}}>
        <h1 className="display-3 fw-bold">üè® Luxury Hotel Seven Hills</h1>
        <p className="lead mt-3">
          Experience World-Class Comfort, Premium Rooms & Affordable Pricing
        </p>

        <button className="btn btn-warning btn-lg mt-3 px-4" onClick={goRooms}>
          View Rooms
        </button>

        <a href="/register" className="btn btn-outline-light btn-lg mt-3 mx-2 px-4">
          Create Account
        </a>
      </div>
    </div>


    {/* FEATURES SECTION */}
    <div className="container text-center py-5">
      <h2 className="fw-bold">Why Choose Us?</h2>
      <p className="text-muted">Premium quality hotel trusted by thousands of guests</p>

      <div className="row mt-4">

        <div className="col-md-3 p-3">
          <div className="card p-3 shadow">
            <h3>‚≠ê Luxury Rooms</h3>
            <p>Fully furnished AC rooms with premium comfort</p>
          </div>
        </div>

        <div className="col-md-3 p-3">
          <div className="card p-3 shadow">
            <h3>üçΩÔ∏è Restaurant</h3>
            <p>Enjoy delicious multi-cuisine meals</p>
          </div>
        </div>

        <div className="col-md-3 p-3">
          <div className="card p-3 shadow">
            <h3>üèä Swimming Pool</h3>
            <p>Relax & refresh in our luxury pool</p>
          </div>
        </div>

        <div className="col-md-3 p-3">
          <div className="card p-3 shadow">
            <h3>üõéÔ∏è 24/7 Service</h3>
            <p>We are always here for you</p>
          </div>
        </div>

      </div>
    </div>


    {/* FOOTER */}
    <div className="bg-dark text-center text-white p-3">
      ¬© 2025 Luxury Hotel Seven Hills | All Rights Reserved
    </div>

    </>
  );
}
