import { Link, useLocation } from "react-router-dom";

export default function AdminNavbar() {

  const loc = useLocation();

  const active = (path) =>
    loc.pathname === path ? "btn-light" : "btn-outline-light";

  return (
    <div className="p-3 bg-dark text-white d-flex align-items-center justify-content-between">

      <h3 className="m-0">Admin Panel</h3>

      <div>
        <Link to="/admin/dashboard" className={`btn mx-1 ${active("/admin/dashboard")}`}>
          Rooms
        </Link>

        <Link to="/admin/bookings" className={`btn mx-1 ${active("/admin/bookings")}`}>
          Bookings
        </Link>

        <Link to="/admin/payments" className={`btn mx-1 ${active("/admin/payments")}`}>
          Payments
        </Link>
      </div>

    </div>
  );
}
