import axios from "axios";
import { useEffect, useState } from "react";
import AdminNavbar from "../components/AdminNavbar";

export default function AdminPayments(){

  const [payments,setPayments] = useState([]);
  const [preview,setPreview] = useState(null);

  const token = localStorage.getItem("adminToken");

  const loadPayments = ()=>{
    axios.get("http://localhost:5000/admin/payments",{
      headers:{ Authorization:`Bearer ${token}` }
    })
    .then(res=>setPayments(res.data))
    .catch(err => console.log(err));
  };

  useEffect(()=>{ loadPayments(); },[]);


  // APPROVE PAYMENT
  const approvePayment = (id)=>{
    axios.put(`http://localhost:5000/admin/payment/approve/${id}`,{},{
      headers:{ Authorization:`Bearer ${token}` }
    })
    .then(()=>{
      alert("Payment Approved");
      loadPayments();
    });
  };


  // REJECT PAYMENT
  const rejectPayment = (id)=>{
    if(!window.confirm("Reject payment?")) return;

    axios.put(`http://localhost:5000/admin/payment/reject/${id}`,{},{
      headers:{ Authorization:`Bearer ${token}` }
    })
    .then(()=>{
      alert("Payment Rejected");
      loadPayments();
    });
  };


  return(
    <div className="container mt-4">

      <AdminNavbar />

      <h2>Admin — Manage Payments</h2>
      <hr/>

      <table className="table table-bordered table-striped">
        <thead>
          <tr>
            <th>User</th>
            <th>Room</th>
            <th>Transaction</th>
            <th>Proof</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>
          {payments.map(p=>(
            <tr key={p.id}>
              <td>{p.userName}</td>
              <td>{p.roomName}</td>
              <td>{p.transactionId || "—"}</td>

              <td>
                {p.paymentProof ? (
                  <button
                    className="btn btn-primary btn-sm"
                    onClick={()=>setPreview(`http://localhost:5000/payments/${p.paymentProof}`)}
                  >
                    View
                  </button>
                ) : "No File"}
              </td>

              <td>
                <span className={
                  p.paymentStatus === "Paid"
                  ? "badge bg-success"
                  : p.paymentStatus === "Rejected"
                  ? "badge bg-danger"
                  : "badge bg-warning"
                }>
                  {p.paymentStatus}
                </span>
              </td>

              <td>
                {p.paymentStatus === "Pending Verification" && (
                  <>
                    <button
                      className="btn btn-success btn-sm mx-1"
                      onClick={()=>approvePayment(p.id)}>
                      Approve
                    </button>

                    <button
                      className="btn btn-danger btn-sm"
                      onClick={()=>rejectPayment(p.id)}>
                      Reject
                    </button>
                  </>
                )}

                {p.paymentStatus !== "Pending Verification" && "—"}
              </td>

            </tr>
          ))}
        </tbody>
      </table>


      {/* ================= IMAGE POPUP ================= */}
      {preview && (
        <div style={{
          position:"fixed",
          inset:0,
          background:"rgba(0,0,0,.7)",
          display:"flex",
          alignItems:"center",
          justifyContent:"center",
          zIndex:1000
        }}>
          
          <div style={{
            background:"#fff",
            padding:"10px",
            borderRadius:"10px",
            textAlign:"center",
            maxWidth:"90%",
            maxHeight:"90%"
          }}>
            
            <img 
              src={preview} 
              alt="Payment Proof"
              style={{maxWidth:"100%", maxHeight:"80vh", borderRadius:"10px"}}
            />

            <br/>

            <button
              className="btn btn-danger mt-3"
              onClick={()=>setPreview(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}

    </div>
  );
}
