import axios from "axios";
import { useEffect, useState } from "react";
import AdminNavbar from "../components/AdminNavbar";

export default function AdminBookings(){

  const [bookings,setBookings] = useState([]);
  const token = localStorage.getItem("adminToken");

  const loadBookings = ()=>{
    axios.get("http://localhost:5000/admin/bookings",{
      headers:{ Authorization:`Bearer ${token}` }
    })
    .then(res => setBookings(res.data));
  };

  useEffect(()=>{ loadBookings(); },[]);

  const approve = (id)=>{
    axios.put(`http://localhost:5000/admin/approve/${id}`)
    .then(()=> loadBookings());
  };

  const reject = (id)=>{
    axios.put(`http://localhost:5000/admin/reject/${id}`)
    .then(()=> loadBookings());
  };

  const del = (id)=>{
    if(!window.confirm("Delete Booking?")) return;
    axios.delete(`http://localhost:5000/admin/bookings/${id}`)
    .then(()=> loadBookings());
  };

  return(
    <>
      <AdminNavbar/>
      <div className="container mt-4">

        <h2>Admin — Manage Bookings</h2>
        <hr/>

        <table className="table table-bordered">
          <thead>
            <tr>
              <th>Customer</th>
              <th>Room</th>
              <th>Dates</th>
              <th>Status</th>
              <th>Payment</th>
              <th>Action</th>
            </tr>
          </thead>

          <tbody>
          {bookings.map(b=>(
            <tr key={b.id}>
              <td>{b.customerName}</td>
              <td>{b.roomName}</td>
              <td>{b.checkIn} → {b.checkOut}</td>

              <td>
                <span className={`badge 
                  ${b.status==="Approved"?"bg-success":
                  b.status==="Rejected"?"bg-danger":"bg-warning"}`}>
                  {b.status}
                </span>
              </td>

              <td>{b.paymentStatus}</td>

              <td>
                {b.status==="Pending" && (
                  <>
                    <button className="btn btn-success btn-sm mx-1"
                      onClick={()=>approve(b.id)}>
                      Approve
                    </button>

                    <button className="btn btn-danger btn-sm"
                      onClick={()=>reject(b.id)}>
                      Reject
                    </button>
                  </>
                )}
                <button className="btn btn-dark btn-sm mx-1"
                  onClick={()=>del(b.id)}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
          </tbody>
        </table>
      </div>
    </>
  );
}
