import axios from "axios";
import { useEffect, useState } from "react";

export default function AdminBookings(){

  const [bookings,setBookings] = useState([]);
  const token = localStorage.getItem("adminToken");

  const loadBookings = ()=>{
    axios.get("http://localhost:5000/admin/bookings",{
      headers:{ Authorization:`Bearer ${token}` }
    }).then(res=>setBookings(res.data));
  };

  useEffect(()=>{ loadBookings(); },[]);

  const approveBooking = (id)=>{
    axios.put(`http://localhost:5000/admin/approve/${id}`,{},{
      headers:{ Authorization:`Bearer ${token}` }
    }).then(()=>{
      alert("Approved");
      loadBookings();
    });
  };

  const rejectBooking = (id)=>{
    if(!window.confirm("Reject booking?")) return;
    axios.put(`http://localhost:5000/admin/reject/${id}`,{},{
      headers:{ Authorization:`Bearer ${token}` }
    }).then(()=>{
      alert("Rejected & Room Released");
      loadBookings();
    });
  };

  const deleteBooking = (id)=>{
    if(!window.confirm("Delete booking?")) return;
    axios.delete(`http://localhost:5000/admin/bookings/${id}`,{
      headers:{ Authorization:`Bearer ${token}` }
    }).then(()=>{
      alert("Deleted & Room Released");
      loadBookings();
    });
  };

  return(
    <div className="container mt-4">
      <h2>Admin — Manage Bookings</h2>
      <hr/>

      <table className="table table-bordered table-striped">
        <thead>
          <tr>
            <th>Customer</th>
            <th>Room</th>
            <th>Dates</th>
            <th>Status</th>
            <th>Payment</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>
          {bookings.map(b=>(
            <tr key={b.id}>
              <td>{b.customerName}</td>
              <td>{b.roomName}</td>
              <td>{b.checkIn} → {b.checkOut}</td>

              <td>
                <span className={`badge ${
                  b.status==="Approved" ? "bg-success" :
                  b.status==="Rejected" ? "bg-danger":"bg-warning"
                }`}>
                  {b.status}
                </span>
              </td>

              <td>
                <span className={`badge ${
                  b.paymentStatus==="Paid" ? "bg-success" :
                  b.paymentStatus==="Rejected" ? "bg-danger":"bg-secondary"
                }`}>
                  {b.paymentStatus}
                </span>
              </td>

              <td>
                {b.status==="Pending" && (
                  <>
                    <button className="btn btn-success btn-sm mx-1"
                      onClick={()=>approveBooking(b.id)}>Approve</button>

                    <button className="btn btn-warning btn-sm mx-1"
                      onClick={()=>rejectBooking(b.id)}>Reject</button>
                  </>
                )}

                <button className="btn btn-danger btn-sm"
                  onClick={()=>deleteBooking(b.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
