import axios from "axios";
import { useParams } from "react-router-dom";
import { useState } from "react";

function Booking(){

  const {id} = useParams();
  const [customerName,setName] = useState("");
  const [checkIn,setCheckIn] = useState("");
  const [checkOut,setCheckOut] = useState("");

  function book(){
    axios.post("http://localhost:5000/book",{
      customerName,
      roomId:id,
      checkIn,
      checkOut
    })
    .then(()=> alert("Booking Successful!"))
    .catch(err=>console.log(err));
  }

  function cancelBooking(){
    axios.put(`http://localhost:5000/cancel-booking/${id}`)
    .then(()=> alert("Booking Cancelled"))
    .catch(err=>console.log(err));
  }

  return(
    <div className="container mt-4">
      <h2>Book Room</h2>

      <input 
        className="form-control mt-2"
        placeholder="Your Name"
        onChange={(e)=>setName(e.target.value)}
      />

      <label className="mt-3">Check In</label>
      <input 
        type="date" 
        className="form-control"
        onChange={(e)=>setCheckIn(e.target.value)} 
      />

      <label className="mt-3">Check Out</label>
      <input 
        type="date" 
        className="form-control"
        onChange={(e)=>setCheckOut(e.target.value)} 
      />

      <button className="btn btn-success mt-3" onClick={book}>
        Confirm Booking
      </button>

      <button className="btn btn-danger mt-3 mx-2" onClick={cancelBooking}>
        Cancel Booking
      </button>
    </div>
  );
}

export default Booking;
