import axios from "axios";
import { useEffect, useState } from "react";

export default function Rooms() {

  const [rooms, setRooms] = useState([]);

  const [filters, setFilters] = useState({
    category: "",
    min: "",
    max: "",
    people: "",
    available: false,
    search: "",
    sort: ""
  });

  // Load Rooms
  const loadRooms = () => {
    axios.get("http://localhost:5000/filter-rooms", { params: filters })
      .then(res => {
        if (Array.isArray(res.data)) {
          setRooms(res.data);
        } else {
          console.log("Unexpected response", res.data);
          setRooms([]);
        }
      })
      .catch(err => {
        console.log("Error fetching rooms", err);
        setRooms([]);
      });
  };

  // Reload when filters change
  useEffect(() => {
    loadRooms();
  }, [filters]);


  return (
    <div className="container mt-4">

      <h2 className="text-center mb-4">Find Your Perfect Room üè®</h2>

      {/* FILTER BAR */}
      <div className="card p-3 shadow mb-4">

        <div className="row">

          {/* CATEGORY */}
          <div className="col-md-2">
            <label>Category</label>
            <select
              className="form-control"
              onChange={(e) => setFilters({ ...filters, category: e.target.value })}
            >
              <option value="">All</option>
              <option value="Standard">Standard</option>
              <option value="Deluxe">Deluxe</option>
              <option value="Suite">Suite</option>
            </select>
          </div>

          {/* MIN PRICE */}
          <div className="col-md-2">
            <label>Min Price</label>
            <input
              type="number"
              className="form-control"
              placeholder="1000"
              onChange={(e) => setFilters({ ...filters, min: e.target.value })}
            />
          </div>

          {/* MAX PRICE */}
          <div className="col-md-2">
            <label>Max Price</label>
            <input
              type="number"
              className="form-control"
              placeholder="5000"
              onChange={(e) => setFilters({ ...filters, max: e.target.value })}
            />
          </div>

          {/* PEOPLE */}
          <div className="col-md-2">
            <label>People</label>
            <input
              type="number"
              className="form-control"
              placeholder="2"
              onChange={(e) => setFilters({ ...filters, people: e.target.value })}
            />
          </div>

          {/* AVAILABLE */}
          <div className="col-md-2">
            <label>Available Only</label><br/>
            <input
              type="checkbox"
              className="form-check-input"
              onChange={(e) => setFilters({ ...filters, available: e.target.checked })}
            />
          </div>

          {/* SORT */}
          <div className="col-md-2">
            <label>Sort</label>
            <select
              className="form-control"
              onChange={(e) => setFilters({ ...filters, sort: e.target.value })}
            >
              <option value="">None</option>
              <option value="low">Price Low ‚Üí High</option>
              <option value="high">Price High ‚Üí Low</option>
            </select>
          </div>

        </div>

        {/* SEARCH */}
        <div className="mt-3">
          <input
            className="form-control"
            placeholder="Search room name..."
            onChange={(e) => setFilters({ ...filters, search: e.target.value })}
          />
        </div>

      </div>


      {/* ROOMS LIST */}
      <div className="row">

        {rooms.length === 0 && (
          <h4 className="text-center text-danger">No rooms found üòî</h4>
        )}

        {rooms.map(room => (
          <div className="col-md-4" key={room.id}>
            <div className="card shadow p-3 mb-4">

              <img
                src={`http://localhost:5000/uploads/${room.image}`}
                className="w-100"
                height="220"
                style={{ objectFit: "cover" }}
                alt={room.name}
              />

              <h3>{room.name}</h3>
              <p>‚Çπ{room.price} / Night</p>
              <p>Capacity: {room.capacity} People</p>

              <span className={`badge ${room.available ? "bg-success" : "bg-danger"}`}>
                {room.available ? "Available" : "Booked"}
              </span>

              {room.available && (
                <a href={`/book/${room.id}`} className="btn btn-primary mt-3">
                  Book Now
                </a>
              )}

            </div>
          </div>
        ))}

      </div>

    </div>
  );
}
