import axios from "axios";
import { useEffect, useState } from "react";

function Rooms(){
  const [rooms,setRooms] = useState([]);

  useEffect(()=>{
    axios.get("http://localhost:5000/rooms")
    .then(res=>setRooms(res.data));
  },[]);

  return(
    <div className="container mt-4">
      <h2 className="text-center mb-4">Available Rooms</h2>

      <div className="row">
        {rooms.map(room=>(
          <div className="col-md-4" key={room.id}>
            <div className="card shadow p-3 mb-4">
  <div id={`carousel${room.id}`} className="carousel slide">
  <div className="carousel-inner">

    <div className="carousel-item active">
      <img 
        src={`http://localhost:5000/uploads/${room.image}`} 
        className="d-block w-100"
        height="200"
        style={{objectFit:"cover"}}
        alt="room"
      />
    </div>

    {/* If you later add more images, add here */}

  </div>
</div>


              <h3>{room.name}</h3>
              <p>â‚¹{room.price}</p>
              <p>Capacity: {room.capacity}</p>

              <span className={`badge ${room.available ? "bg-success":"bg-danger"}`}>
                {room.available ? "Available" : "Booked"}
              </span>

              {room.available &&
                <a href={`/book/${room.id}`} className="btn btn-primary mt-3">
                  Book Now
                </a>
              }
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
export default Rooms;
