import axios from "axios";
import { useEffect, useState } from "react";

export default function AdminDashboard(){

  const [rooms,setRooms] = useState([]);
  const [form,setForm] = useState({name:"",price:"",capacity:""});
  const [editId,setEditId] = useState(null);

  const token = localStorage.getItem("token");

  const loadRooms = ()=>{
    axios.get("http://localhost:5000/admin/rooms",{
      headers:{ Authorization: token }
    })
    .then(res => setRooms(res.data))
    .catch(()=>window.location="/admin/login");
  };

  useEffect(()=>{ loadRooms(); },[]);useEffect(()=>{
  loadRooms();
},[]); // eslint-disable-next-line


  const handleChange = e =>{
    setForm({...form,[e.target.name]:e.target.value});
  };

  // ADD ROOM
  const addRoom = ()=>{

  const fd = new FormData();
  fd.append("name",form.name);
  fd.append("price",form.price);
  fd.append("capacity",form.capacity);
  fd.append("image",form.image);

  axios.post("http://localhost:5000/admin/rooms",fd,{
    headers:{
      Authorization: token,
      "Content-Type":"multipart/form-data"
    }
  })
  .then(()=>{
    alert("Room Added");
    setForm({name:"",price:"",capacity:""});
    loadRooms();
  })
  .catch(err=>console.log(err));
}


  // SET EDIT MODE
  const startEdit = room =>{
    setEditId(room.id);
    setForm({name:room.name,price:room.price,capacity:room.capacity});
  };
  <input 
  type="file"
  className="form-control mt-2"
  onChange={(e)=>setForm({...form,image:e.target.files[0]})}
/>


  // UPDATE ROOM
  const updateRoom = ()=>{

  const fd = new FormData();
  fd.append("name",form.name);
  fd.append("price",form.price);
  fd.append("capacity",form.capacity);

  if(form.image){
    fd.append("image",form.image);
  }

  axios.put(`http://localhost:5000/admin/rooms/${editId}`,fd,{
    headers:{
      Authorization: token,
      "Content-Type":"multipart/form-data"
    }
  }).then(()=>{
    alert("Room Updated");
    setEditId(null);
    setForm({name:"",price:"",capacity:"",image:null});
    loadRooms();
  })
}

  // DELETE ROOM
  const deleteRoom = id =>{
    if(!window.confirm("Delete Room?")) return;

    axios.delete(`http://localhost:5000/admin/rooms/${id}`,{
      headers:{ Authorization: token }
    }).then(()=>{
      alert("Room Deleted");
      loadRooms();
    });
  };

  const logout = ()=>{
    localStorage.removeItem("token");
    window.location="/admin/login";
  };

  return(
    <div className="container mt-4">
      <h2>Admin Dashboard</h2>
      <button className="btn btn-danger mb-3" onClick={logout}>Logout</button>

      <h4>{editId ? "Edit Room" : "Add Room"}</h4>

      <input className="form-control mt-2" placeholder="Name"
        name="name" value={form.name} onChange={handleChange}/>

      <input className="form-control mt-2" placeholder="Price"
        name="price" value={form.price} onChange={handleChange}/>

      <input className="form-control mt-2" placeholder="Capacity"
        name="capacity" value={form.capacity} onChange={handleChange}/>
      <input className="form-control mt-2" type="file"
      onChange={e=>setForm({...form, image:e.target.files[0]})} />

      {editId ?
        <button className="btn btn-warning mt-2" onClick={updateRoom}>Update Room</button>
        :
        <button className="btn btn-success mt-2" onClick={addRoom}>Add Room</button>
      }

      <hr/>

      <table className="table table-bordered mt-3">
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Capacity</th>
            <th>Available</th>
            <th>Actions</th>
          </tr>
        </thead>

        <tbody>
          {rooms.map(r=>(
            <tr key={r.id}>
              <td>{r.name}</td>
              <td>{r.price}</td>
              <td>{r.capacity}</td>
              <td>{r.available ? "Yes" : "No"}</td>
              <td>
                <button className="btn btn-primary btn-sm mx-1" onClick={()=>startEdit(r)}>Edit</button>
                <button className="btn btn-danger btn-sm" onClick={()=>deleteRoom(r.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

    </div>
  );
}
