import axios from "axios";
import { useEffect, useState } from "react";

export default function Bookings(){

  const [bookings, setBookings] = useState([]);

  useEffect(() => {

    const userId = localStorage.getItem("userId");
    if(!userId){
      alert("Please login again");
      return;
    }

    axios.get(`http://localhost:5000/user/bookings/${userId}`)
      .then(res => {
        if(Array.isArray(res.data)){
          setBookings(res.data);
        } else {
          setBookings([]);   // prevent .map crash
        }
      })
      .catch(err => {
        console.log(err);
        setBookings([]);
      });

  }, []);

  const cancelBooking = (id) =>{
    axios.put(`http://localhost:5000/cancel-booking/${id}`)
      .then(()=>{
        alert("Booking Cancelled");
        window.location.reload();
      });
  };

  return(
    <div className="container mt-4">
      <h2>Booking History</h2>

      {bookings.length === 0 ? (
        <h4 className="text-center text-danger mt-3">
          No Bookings Found ðŸ˜”
        </h4>
      ) : (
        <table className="table table-bordered mt-3">
          <thead>
            <tr>
              <th>Name</th>
              <th>Room</th>
              <th>CheckIn</th>
              <th>CheckOut</th>
              <th>Status</th>
              <th>Cancel</th>
            </tr>
          </thead>

          <tbody>
            {bookings.map(b => (
              <tr key={b.id}>
                <td>{b.customerName}</td>
                <td>{b.roomName || b.roomId}</td>
                <td>{b.checkIn}</td>
                <td>{b.checkOut}</td>
                <td>{b.status}</td>

                <td>
                  {b.status === "Booked" ?
                    <button className="btn btn-danger"
                      onClick={()=>cancelBooking(b.id)}>
                      Cancel
                    </button>
                    :
                    "Cancelled"
                  }
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

    </div>
  );
}
