import axios from "axios";
import { useEffect, useState } from "react";

export default function AdminPayments(){

  const [payments,setPayments] = useState([]);

  useEffect(()=>{
    axios.get("http://localhost:5000/admin/payments",{
      headers:{ Authorization: `Bearer ${localStorage.getItem("adminToken")}` }
    })
    .then(res=> setPayments(res.data));
  },[]);

  function update(id,status){
    axios.put(`http://localhost:5000/admin/payment-status/${id}`,
      {status},
      {headers:{ Authorization: `Bearer ${localStorage.getItem("adminToken")}` }}
    ).then(()=>{
      alert("Updated");
      window.location.reload();
    })
  }

  return(
    <div className="container mt-4">
      <h2>Payment Requests</h2>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>User</th>
            <th>Room</th>
            <th>CheckIn</th>
            <th>CheckOut</th>
            <th>Proof</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>
          {payments.map(p=>(
            <tr key={p.id}>
              <td>{p.userName}</td>
              <td>{p.roomName}</td>
              <td>{p.checkIn}</td>
              <td>{p.checkOut}</td>

              <td>
                <a href={`http://localhost:5000/uploads/${p.paymentProof}`} target="_blank">
                  View Proof
                </a>
              </td>

              <td>
                <button className="btn btn-success btn-sm"
                  onClick={()=>update(p.id,"Paid")}>
                  Approve
                </button>

                <button className="btn btn-danger btn-sm ms-2"
                  onClick={()=>update(p.id,"Rejected")}>
                  Reject
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

    </div>
  );
}
