import axios from "axios";
import { useEffect, useState } from "react";

export default function AdminDashboard(){

  const [rooms,setRooms] = useState([]);
  const [bookings,setBookings] = useState([]);
  const [payments,setPayments] = useState([]);

  const token = localStorage.getItem("adminToken");


  // LOAD ROOMS
  const loadRooms = ()=>{
    axios.get("http://localhost:5000/admin/rooms",{
      headers:{ Authorization: `Bearer ${token}` }
    })
    .then(res => setRooms(res.data))
    .catch(()=>alert("Rooms load failed"));
  };


  // LOAD BOOKINGS
  const loadBookings = ()=>{
    axios.get("http://localhost:5000/admin/bookings",{
      headers:{ Authorization: `Bearer ${token}` }
    })
    .then(res => setBookings(res.data))
    .catch(()=>alert("Bookings load failed"));
  };


  // LOAD PAYMENTS (same API because payment info comes with bookings)
  const loadPayments = ()=>{
    axios.get("http://localhost:5000/admin/bookings",{
      headers:{ Authorization: `Bearer ${token}` }
    })
    .then(res => setPayments(res.data))
    .catch(()=>alert("Payments load failed"));
  };


  useEffect(()=>{
    loadRooms();
    loadBookings();
    loadPayments();
  },[]);


  // APPROVE BOOKING
  const approveBooking = (id)=>{
    axios.put(`http://localhost:5000/admin/approve/${id}`,{},{
      headers:{ Authorization: `Bearer ${token}` }
    })
    .then(()=>{
      alert("Booking Approved");
      loadBookings();
      loadRooms();
    });
  };


  // REJECT BOOKING
  const rejectBooking = (id)=>{
    if(!window.confirm("Reject booking?")) return;

    axios.put(`http://localhost:5000/admin/reject/${id}`,{},{
      headers:{ Authorization: `Bearer ${token}` }
    })
    .then(()=>{
      alert("Booking Rejected & Room Released");
      loadBookings();
      loadRooms();
    });
  };


  // APPROVE PAYMENT
  const approvePayment = (id)=>{
    axios.put(`http://localhost:5000/admin/payment/approve/${id}`,{},{
      headers:{ Authorization: `Bearer ${token}` }
    })
    .then(()=>{
      alert("Payment Approved");
      loadPayments();
      loadBookings();
    });
  };


  // REJECT PAYMENT
  const rejectPayment = (id)=>{
    if(!window.confirm("Reject payment?")) return;

    axios.put(`http://localhost:5000/admin/payment/reject/${id}`,{},{
      headers:{ Authorization: `Bearer ${token}` }
    })
    .then(()=>{
      alert("Payment Rejected");
      loadPayments();
      loadBookings();
    });
  };


  return(
    <div className="container mt-4">

      <h2>Admin Dashboard</h2>


      {/* ================= ROOMS ================= */}
      <h3>Rooms</h3>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Name</th>
            <th>Price</th>
            <th>Capacity</th>
            <th>Status</th>
          </tr>
        </thead>

        <tbody>
          {rooms.map(r=>(
            <tr key={r.id}>
              <td>{r.name}</td>
              <td>{r.price}</td>
              <td>{r.capacity}</td>
              <td>
                <span className={`badge ${r.available? "bg-success":"bg-danger"}`}>
                  {r.available ? "Available" : "Booked"}
                </span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>


      {/* ================= BOOKINGS ================= */}
      <hr/>
      <h3>Bookings</h3>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Customer</th>
            <th>Room</th>
            <th>Dates</th>
            <th>Status</th>
            <th>Payment</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>
          {bookings.map(b=>(
            <tr key={b.id}>
              <td>{b.customerName}</td>
              <td>{b.roomName}</td>
              <td>{b.checkIn} → {b.checkOut}</td>

              <td>
                <span className={
                  b.status === "Approved"
                  ? "badge bg-success"
                  : b.status === "Rejected"
                  ? "badge bg-danger"
                  : "badge bg-warning"
                }>
                  {b.status}
                </span>
              </td>

              <td>
                <span className={
                  b.paymentStatus === "Paid"
                  ? "badge bg-success"
                  : b.paymentStatus === "Rejected"
                  ? "badge bg-danger"
                  : "badge bg-secondary"
                }>
                  {b.paymentStatus}
                </span>
              </td>

              <td>
                {b.status === "Pending" ? (
                  <>
                    <button className="btn btn-success btn-sm mx-1"
                      onClick={()=>approveBooking(b.id)}>
                      Approve
                    </button>

                    <button className="btn btn-danger btn-sm"
                      onClick={()=>rejectBooking(b.id)}>
                      Reject
                    </button>
                  </>
                ) : "—"}
              </td>

            </tr>
          ))}
        </tbody>
      </table>



      {/* ================= PAYMENTS ================= */}
      <hr/>
      <h3>Payments</h3>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>User</th>
            <th>Room</th>
            <th>Transaction</th>
            <th>Proof</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>

        <tbody>
          {payments.map(p=>(
            <tr key={p.id}>
              <td>{p.customerName}</td>
              <td>{p.roomName}</td>
              <td>{p.transactionId || "—"}</td>

              <td>
                {p.paymentProof ? (
                  <a
                    href={`http://localhost:5000/payments/${p.paymentProof}`}
                    target="_blank"
                    rel="noreferrer"
                    className="btn btn-secondary btn-sm">
                    View
                  </a>
                ) : "No File"}
              </td>

              <td>
                <span className={
                  p.paymentStatus === "Paid"
                  ? "badge bg-success"
                  : p.paymentStatus === "Rejected"
                  ? "badge bg-danger"
                  : "badge bg-warning"
                }>
                  {p.paymentStatus}
                </span>
              </td>

              <td>
                {p.paymentStatus === "Pending Verification" ? (
                  <>
                    <button
                      className="btn btn-success btn-sm mx-1"
                      onClick={()=>approvePayment(p.id)}>
                      Approve
                    </button>

                    <button
                      className="btn btn-danger btn-sm"
                      onClick={()=>rejectPayment(p.id)}>
                      Reject
                    </button>
                  </>
                ) : "—"}
              </td>

            </tr>
          ))}
        </tbody>
      </table>

    </div>
  );
}
